import styled from 'styled-components';

export const Header = styled.section`
    z-index: 5;
    position: sticky;
    top: 100px;
    left:0;
    right: 0;
    /* pointer-events: none; */
    display: flex;
    justify-content: center;
    align-items: center;
    flex-direction: column;
    margin: 0 auto;

    font-family: 'Lato', sans-serif;
    color: orange;

    h1, p {
        position: relative;
        z-index: -1;
    }

    h1 {
        text-align: center;
        text-transform: uppercase;
        display: block;
        font-size: 40px;
        font-weight: 300;
        max-width: 768px;
        letter-spacing: 3px;
    }

    p {
        margin-top: 16px;
        color: rgba(255, 255, 255, 0.75);
        letter-spacing: 2px;
        font-weight: 300;
        font-size: 18px;
        -webkit-font-smoothing: antialiased;
        text-align: center;
    }
`;

export const Body = styled.section`
    background-color: white;
    border-radius: 8px;
    padding: 8px;
    position: relative;
    height: 1900px;
    z-index: 20;
    margin-top: 48px;
`;
