import styled from 'styled-components';

export const About = styled.div`

`;

export const Header = styled.section`
    position: sticky;
    top: 100px;
    z-index: 5;

    display: grid;
    justify-content: center;
    align-items: center;
    margin: 0 auto;
    padding: 100px 0;

    font-family: 'Lato', sans-serif;
    color: #fff;

    h1 {
        text-align: center;
        text-transform: uppercase;
        display: block;
        font-size: 40px;
        font-weight: 300;
        max-width: 768px;
        letter-spacing: 3px;
    }

    p {
        margin-top: 20px;
        color: rgba(255, 255, 255, 0.75);
        letter-spacing: 2px;
        font-weight: 300;
        font-size: 18px;
        -webkit-font-smoothing: antialiased;
        text-align: center;
    }
`;

export const Body = styled.section`
    background-color: white;
    border-radius: 12px;
    position: relative;
    height: 1900px;
    z-index: 10;
`;
